import React, { useEffect } from 'react';
import * as d3 from 'd3';

function UserFlowChart() {
  useEffect(() => {

    // Define the nodes and links
const nodes = [
    { id: "Auth System", group: 1 },
    { id: "Profile Page", group: 2 },
    { id: "Bet Page", group: 2 },
    { id: "History Page", group: 2 },
    { id: "Feature 1", group: 3 },
    { id: "Feature 2", group: 3 },
    // Add more features as necessary
  ];
  
  const links = [
    { source: "Auth System", target: "Profile Page" },
    { source: "Auth System", target: "Bet Page" },
    { source: "Auth System", target: "History Page" },
    { source: "Profile Page", target: "Feature 1" },
    { source: "Profile Page", target: "Feature 2" },
    // Add more links between pages and features
  ];
  
  // Set up the D3 environment
  const width = 800;
  const height = 600;
  const svg = d3.create("svg")
    .attr("width", width)
    .attr("height", height);
  
  // Create a force simulation
  const simulation = d3.forceSimulation(nodes)
    .force("link", d3.forceLink(links).id(d => d.id))
    .force("charge", d3.forceManyBody())
    .force("center", d3.forceCenter(width / 2, height / 2));
  
  // Add lines for links
  const link = svg.append("g")
    .attr("stroke", "#999")
    .selectAll("line")
    .data(links)
    .join("line")
    .attr("stroke-width", d => Math.sqrt(d.value));
  
  // Add circles for nodes
  const node = svg.append("g")
    .attr("stroke", "#fff")
    .attr("stroke-width", 1.5)
    .selectAll("circle")
    .data(nodes)
    .join("circle")
    .attr("r", 5)
    .attr("fill", d => color(d.group))
    .call(drag(simulation));
  
  // Add labels
  node.append("title")
    .text(d => d.id);
  
  // Handle the simulation
  simulation.on("tick", () => {
    link
      .attr("x1", d => d.source.x)
      .attr("y1", d => d.source.y)
      .attr("x2", d => d.target.x)
      .attr("y2", d => d.target.y);
  
    node
      .attr("cx", d => d.x)
      .attr("cy", d => d.y);
  });
  
  // Drag functionality
  function drag(simulation) {
    function dragstarted(event) {
      if (!event.active) simulation.alphaTarget(0.3).restart();
      event.subject.fx = event.subject.x;
      event.subject.fy = event.subject.y;
    }
  
    function dragged(event) {
      event.subject.fx = event.x;
      event.subject.fy = event.y;
    }
  
    function dragended(event) {
      if (!event.active) simulation.alphaTarget(0);
      event.subject.fx = null;
      event.subject.fy = null;
    }
  
    return d3.drag()
      .on("start", dragstarted)
      .on("drag", dragged)
      .on("end", dragended);
  }
  
  // Add the SVG to the body
  document.body.appendChild(svg.node());
  
    // Your D3 code goes here. Adapt the previous example to fit within this useEffect hook.
    // Make sure to use the d3 module imported above, and adjust the DOM manipulation to fit React's model.
  }, []);

  return (
    <div>
      <svg ref={ref => (this.svg = ref)} width={800} height={600} />
    </div>
  );
}

export default UserFlowChart;
